import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faDownload, faShareAlt, faEdit, faTrash } from '@fortawesome/free-solid-svg-icons';
import { AiOutlineEye, AiFillEye } from 'react-icons/ai';


export default class AssetTableRow extends Component {
  constructor(props) {
    super(props);
    this.TransferAsset = this.TransferAsset.bind(this);
    this.handleImageClick = this.handleImageClick.bind(this);
    this.handleDownloadClick = this.handleDownloadClick.bind(this);
    this.handleShareClick = this.handleShareClick.bind(this);
    this.copyToClipboard = this.copyToClipboard.bind(this);

    this.state = {
      showShareModal: false,
    };
  }

  async TransferAsset() {
    try {
      const response = await axios.post('/api/transfer-asset', {
        assetId: this.props.obj.ID,
      });
      alert('Asset transferred successfully!');
    } catch (error) {
      console.error('Error transferring asset:', error);
      alert('Failed to transfer asset.');
    }
  }

  handleImageClick(documentURL) {
    if (documentURL) {
      const pinataGatewayUrl = `https://gateway.pinata.cloud/ipfs/${documentURL}`;
      window.open(pinataGatewayUrl, '_blank');
    } else {
      console.error('Invalid Document URL');
    }
  }

  handleDownloadClick(documentURL) {
    if (documentURL) {
      const pinataGatewayUrl = `https://gateway.pinata.cloud/ipfs/${documentURL}`;

      axios({
        url: pinataGatewayUrl,
        method: 'GET',
        responseType: 'blob',
      })
        .then((response) => {
          const url = window.URL.createObjectURL(new Blob([response.data]));
          const link = document.createElement('a');
          link.href = url;
          link.setAttribute('download', `document_${this.props.obj.ID}.${this.props.obj.DocumentFormat.toLowerCase()}`);
          document.body.appendChild(link);
          link.click();
          link.remove();
        })
        .catch((error) => {
          console.error('Error downloading document:', error);
          alert('Failed to download document.');
        });
    } else {
      console.error('Invalid Document URL');
    }
  }

  handleShareClick() {
    this.setState({ showShareModal: true });
  }

  handleCloseModal = () => {
    this.setState({ showShareModal: false });
  }

  copyToClipboard() {
    const shareableLink = `https://your-website.com/assets/${this.props.obj.ID}`;
    navigator.clipboard.writeText(shareableLink).then(() => {
      alert('Link copied to clipboard!');
    }).catch((err) => {
      console.error('Failed to copy: ', err);
    });
  }

  render() {
    const { obj } = this.props;
    const { showShareModal } = this.state;

    return (
      <>
        <tr>
          <td>{obj.ID}</td>
          <td>{obj.Owner}</td>
          <td>{obj.DocumentFormat}</td>
          <td>{obj.DocumentType}</td>
          <td>
            <a
              href="#"
              onClick={(e) => {
                e.preventDefault();
                this.handleImageClick(obj.DocumentURL);
              }}
            >
              View Document &nbsp; <AiOutlineEye size={25} color="#512DA8" />
            </a>
          </td>
          <td>
            <Link className="edit-link bg-green-300" to={`/edit-asset/${obj.ID}`}>
              <FontAwesomeIcon icon={faEdit} /> Edit
            </Link>

            <Link className="edit-link bg-red-400" to={`/delete-asset/${obj.ID}`}>
              <FontAwesomeIcon icon={faTrash} /> Delete
            </Link>

            {/* Download Button */}
            <Button
              variant="primary"
              className="edit-link"
              onClick={() => this.handleDownloadClick(obj.DocumentURL)}
            >
              <FontAwesomeIcon icon={faDownload} /> Download
            </Button>

            {/* Share Button */}
            <Button
              variant="info"
              className="edit-link"
              onClick={this.handleShareClick}
            >
              <FontAwesomeIcon icon={faShareAlt} /> &nbsp; Share
            </Button>
          </td>
        </tr>

        {/* Share Modal */}
        <Modal show={showShareModal} onHide={this.handleCloseModal}>
          <Modal.Header closeButton>
            <Modal.Title>Share Asset</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <p>Share this link:</p>
            <button className="btn btn-link" onClick={this.copyToClipboard}>
              Copy Link
            </button>
            <div>
              <p>Share via:</p>
              <a
                href={`https://wa.me/?text=Check%20out%20this%20asset%3A%20${encodeURIComponent(`https://your-website.com/assets/${obj.ID}`)}`}
                target="_blank"
                rel="noopener noreferrer"
                className="btn btn-success"
              >
                <FontAwesomeIcon icon={faShareAlt} /> &nbsp; WhatsApp
              </a>
              {/* Additional share icons can be added here */}
            </div>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={this.handleCloseModal}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
  }
}
